require 'serverspec'

# Required by serverspec
set :backend, :exec

describe user('user1_yaml') do
  it { should exist }
  it { have_login_shell '/bin/true' }
  it { should have_authorized_key 'ssh-rsa  yaml1' }
end

describe user('user2_yaml') do
  it { should exist }
  it { should have_authorized_key 'ssh-rsa  yaml2' }
end

describe user('user3_json') do
  it { should exist }
  it { should have_authorized_key 'ssh-rsa  user3_json' }
end

